        return new Source("{% extends 'base.html.twig' %}

{% block title %}Modifier votre mot de passe{% endblock %}

{% block body %}

<div class=\"my-5 py-5\">
    <div class=\"col-lg-6 mx-auto\">
            {% for flash_error in app.flashes('reset_password_error') %}
                <div class=\"alert alert-danger\" role=\"alert\">{{ flash_error }}</div>
            {% endfor %}
            <h1 class=\"h3 mb-3 font-weight-normal\">Modifier votre mot de passe</h1>

            {{ form_start(requestForm) }}
                {{ form_row(requestForm.email) }}
                <div>
                    <small>
                        Entrez votre adresse email et nous vous enverrons un lien pour changer votre mot de passe.
                    </small>
                </div>

                <button class=\"btn btn-primary mt-3\">Envoyer le mail de récupération</button>
            {{ form_end(requestForm) }}
    </div>           
</div>
{% endblock %}
", "reset_password/request.html.twig", "/var/www/html/Apothéose/07-le-site-dont-vous-etes-le-heros-back/templates/reset_password/request.html.twig");
